import{_ as s,o as n,c as a,R as l}from"./chunks/framework.d45ee533.js";const F=JSON.parse('{"title":"SELECT - 查","description":"","frontmatter":{},"headers":[],"relativePath":"backend/mysql/select.md"}'),p={name:"backend/mysql/select.md"},o=l(`<h1 id="select-查" tabindex="-1">SELECT - 查 <a class="header-anchor" href="#select-查" aria-label="Permalink to &quot;SELECT - 查&quot;">​</a></h1><div class="language-sql"><button title="Copy Code" class="copy"></button><span class="lang">sql</span><pre class="shiki one-dark-pro"><code><span class="line"><span style="color:#C678DD;">SELECT</span><span style="color:#ABB2BF;"> * </span><span style="color:#C678DD;">FROM</span><span style="color:#ABB2BF;"> customers;</span></span></code></pre></div><ol><li>SELECT 可以选择指定的列</li><li>可以对拿到的结果做运算，通过括号可以改变运算符顺序</li><li>AS 可以对表头的名称作修改</li></ol><div class="language-sql"><button title="Copy Code" class="copy"></button><span class="lang">sql</span><pre class="shiki one-dark-pro"><code><span class="line"><span style="color:#C678DD;">SELECT</span></span>
<span class="line"><span style="color:#ABB2BF;">	first_name,</span></span>
<span class="line"><span style="color:#ABB2BF;">    last_name,</span></span>
<span class="line"><span style="color:#ABB2BF;">    points,</span></span>
<span class="line"><span style="color:#ABB2BF;">    (points + </span><span style="color:#D19A66;">1</span><span style="color:#ABB2BF;">) * </span><span style="color:#D19A66;">10</span><span style="color:#ABB2BF;"> </span><span style="color:#C678DD;">AS</span><span style="color:#ABB2BF;"> </span><span style="color:#98C379;">&#39;total points&#39;</span></span>
<span class="line"><span style="color:#C678DD;">FROM</span><span style="color:#ABB2BF;"> customers;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;">-- 对查询的值去重复</span></span>
<span class="line"><span style="color:#C678DD;">select distinct</span><span style="color:#ABB2BF;"> </span><span style="color:#C678DD;">state</span><span style="color:#ABB2BF;"> </span><span style="color:#C678DD;">from</span><span style="color:#ABB2BF;"> customers;</span></span></code></pre></div><h2 id="where" tabindex="-1">WHERE <a class="header-anchor" href="#where" aria-label="Permalink to &quot;WHERE&quot;">​</a></h2><div class="language-sql"><button title="Copy Code" class="copy"></button><span class="lang">sql</span><pre class="shiki one-dark-pro"><code><span class="line"><span style="color:#C678DD;">select</span><span style="color:#ABB2BF;"> * </span><span style="color:#C678DD;">from</span><span style="color:#ABB2BF;"> customers </span><span style="color:#C678DD;">where</span><span style="color:#ABB2BF;"> points </span><span style="color:#56B6C2;">&gt;</span><span style="color:#ABB2BF;"> </span><span style="color:#D19A66;">3000</span><span style="color:#ABB2BF;">;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;">-- 相等 / 不等</span></span>
<span class="line"><span style="color:#C678DD;">select</span><span style="color:#ABB2BF;"> * </span><span style="color:#C678DD;">from</span><span style="color:#ABB2BF;"> customers </span><span style="color:#C678DD;">where</span><span style="color:#ABB2BF;"> </span><span style="color:#C678DD;">state</span><span style="color:#ABB2BF;"> </span><span style="color:#56B6C2;">=</span><span style="color:#ABB2BF;"> </span><span style="color:#98C379;">&#39;VA&#39;</span><span style="color:#ABB2BF;">;</span></span>
<span class="line"><span style="color:#C678DD;">select</span><span style="color:#ABB2BF;"> * </span><span style="color:#C678DD;">from</span><span style="color:#ABB2BF;"> customers </span><span style="color:#C678DD;">where</span><span style="color:#ABB2BF;"> </span><span style="color:#C678DD;">state</span><span style="color:#ABB2BF;"> </span><span style="color:#56B6C2;">&lt;&gt;</span><span style="color:#ABB2BF;"> </span><span style="color:#98C379;">&#39;VA&#39;</span><span style="color:#ABB2BF;">;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;">-- 日期判断</span></span>
<span class="line"><span style="color:#C678DD;">select</span><span style="color:#ABB2BF;"> * </span><span style="color:#C678DD;">from</span><span style="color:#ABB2BF;"> customers </span><span style="color:#C678DD;">where</span><span style="color:#ABB2BF;"> birth_date </span><span style="color:#56B6C2;">&gt;</span><span style="color:#ABB2BF;"> </span><span style="color:#98C379;">&#39;1992-01-01&#39;</span><span style="color:#ABB2BF;">;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;">-- and</span></span>
<span class="line"><span style="color:#C678DD;">select</span><span style="color:#ABB2BF;"> * </span><span style="color:#C678DD;">from</span><span style="color:#ABB2BF;"> customers </span><span style="color:#C678DD;">where</span><span style="color:#ABB2BF;"> birth_date </span><span style="color:#56B6C2;">&gt;</span><span style="color:#ABB2BF;"> </span><span style="color:#98C379;">&#39;1990-01-01&#39;</span><span style="color:#ABB2BF;"> </span><span style="color:#C678DD;">and</span><span style="color:#ABB2BF;"> points </span><span style="color:#56B6C2;">&gt;</span><span style="color:#ABB2BF;"> </span><span style="color:#D19A66;">1000</span><span style="color:#ABB2BF;">;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;">-- or</span></span>
<span class="line"><span style="color:#C678DD;">select</span><span style="color:#ABB2BF;"> * </span><span style="color:#C678DD;">from</span><span style="color:#ABB2BF;"> customers </span><span style="color:#C678DD;">where</span><span style="color:#ABB2BF;"> birth_date </span><span style="color:#56B6C2;">&gt;</span><span style="color:#ABB2BF;"> </span><span style="color:#98C379;">&#39;1990-01-01&#39;</span><span style="color:#ABB2BF;"> </span><span style="color:#C678DD;">or</span><span style="color:#ABB2BF;"> points </span><span style="color:#56B6C2;">&gt;</span><span style="color:#ABB2BF;"> </span><span style="color:#D19A66;">1000</span><span style="color:#ABB2BF;">;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;">-- 组合</span></span>
<span class="line"><span style="color:#C678DD;">select</span><span style="color:#ABB2BF;"> * </span><span style="color:#C678DD;">from</span><span style="color:#ABB2BF;"> customers </span><span style="color:#C678DD;">where</span><span style="color:#ABB2BF;"> birth_date </span><span style="color:#56B6C2;">&gt;</span><span style="color:#ABB2BF;"> </span><span style="color:#98C379;">&#39;1990-01-01&#39;</span><span style="color:#ABB2BF;"> </span><span style="color:#C678DD;">or</span><span style="color:#ABB2BF;"> (points </span><span style="color:#56B6C2;">&gt;</span><span style="color:#ABB2BF;"> </span><span style="color:#D19A66;">1000</span><span style="color:#ABB2BF;"> </span><span style="color:#C678DD;">and</span><span style="color:#ABB2BF;"> </span><span style="color:#C678DD;">state</span><span style="color:#ABB2BF;"> </span><span style="color:#56B6C2;">=</span><span style="color:#ABB2BF;"> </span><span style="color:#98C379;">&#39;VA&#39;</span><span style="color:#ABB2BF;">);</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;">-- not</span></span>
<span class="line"><span style="color:#C678DD;">select</span><span style="color:#ABB2BF;"> * </span><span style="color:#C678DD;">from</span><span style="color:#ABB2BF;"> customers </span><span style="color:#C678DD;">where</span><span style="color:#ABB2BF;"> </span><span style="color:#C678DD;">not</span><span style="color:#ABB2BF;"> (birth_date </span><span style="color:#56B6C2;">&gt;</span><span style="color:#ABB2BF;"> </span><span style="color:#98C379;">&#39;1990-01-01&#39;</span><span style="color:#ABB2BF;"> </span><span style="color:#C678DD;">or</span><span style="color:#ABB2BF;"> (points </span><span style="color:#56B6C2;">&gt;</span><span style="color:#ABB2BF;"> </span><span style="color:#D19A66;">1000</span><span style="color:#ABB2BF;"> </span><span style="color:#C678DD;">and</span><span style="color:#ABB2BF;"> </span><span style="color:#C678DD;">state</span><span style="color:#ABB2BF;"> </span><span style="color:#56B6C2;">=</span><span style="color:#ABB2BF;"> </span><span style="color:#98C379;">&#39;VA&#39;</span><span style="color:#ABB2BF;">));</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;">-- in</span></span>
<span class="line"><span style="color:#C678DD;">select</span><span style="color:#ABB2BF;"> * </span><span style="color:#C678DD;">from</span><span style="color:#ABB2BF;"> customers </span><span style="color:#C678DD;">where</span><span style="color:#ABB2BF;"> </span><span style="color:#C678DD;">state</span><span style="color:#ABB2BF;"> </span><span style="color:#C678DD;">in</span><span style="color:#ABB2BF;"> (</span><span style="color:#98C379;">&#39;VA&#39;</span><span style="color:#ABB2BF;">, </span><span style="color:#98C379;">&#39;FL&#39;</span><span style="color:#ABB2BF;">, </span><span style="color:#98C379;">&#39;GA&#39;</span><span style="color:#ABB2BF;">);</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;">-- between</span></span>
<span class="line"><span style="color:#C678DD;">SELECT</span><span style="color:#ABB2BF;"> * </span><span style="color:#C678DD;">FROM</span><span style="color:#ABB2BF;"> </span><span style="color:#D19A66;">sql_store</span><span style="color:#ABB2BF;">.</span><span style="color:#D19A66;">customers</span><span style="color:#ABB2BF;"> </span><span style="color:#C678DD;">where</span><span style="color:#ABB2BF;"> points </span><span style="color:#C678DD;">between</span><span style="color:#ABB2BF;"> </span><span style="color:#D19A66;">1000</span><span style="color:#ABB2BF;"> </span><span style="color:#C678DD;">and</span><span style="color:#ABB2BF;"> </span><span style="color:#D19A66;">3000</span><span style="color:#ABB2BF;">;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;">-- like</span></span>
<span class="line"><span style="color:#7F848E;font-style:italic;">-- % 表示任意字符，任意数量</span></span>
<span class="line"><span style="color:#7F848E;font-style:italic;">-- _ 表示一个单字符</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;">-- 查找名称中含有 b 的</span></span>
<span class="line"><span style="color:#C678DD;">SELECT</span><span style="color:#ABB2BF;"> * </span><span style="color:#C678DD;">FROM</span><span style="color:#ABB2BF;"> </span><span style="color:#D19A66;">sql_store</span><span style="color:#ABB2BF;">.</span><span style="color:#D19A66;">customers</span><span style="color:#ABB2BF;"> </span><span style="color:#C678DD;">where</span><span style="color:#ABB2BF;"> last_name </span><span style="color:#C678DD;">like</span><span style="color:#ABB2BF;"> </span><span style="color:#98C379;">&#39;%b%&#39;</span><span style="color:#ABB2BF;">;</span></span>
<span class="line"><span style="color:#7F848E;font-style:italic;">-- 查找 b 开头，中间 4个任意字符，结尾为 y 的</span></span>
<span class="line"><span style="color:#C678DD;">SELECT</span><span style="color:#ABB2BF;"> * </span><span style="color:#C678DD;">FROM</span><span style="color:#ABB2BF;"> </span><span style="color:#D19A66;">sql_store</span><span style="color:#ABB2BF;">.</span><span style="color:#D19A66;">customers</span><span style="color:#ABB2BF;"> </span><span style="color:#C678DD;">where</span><span style="color:#ABB2BF;"> last_name </span><span style="color:#C678DD;">like</span><span style="color:#ABB2BF;"> </span><span style="color:#98C379;">&#39;b____y&#39;</span><span style="color:#ABB2BF;">;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;">-- regexp 正则匹配字符串</span></span>
<span class="line"><span style="color:#C678DD;">SELECT</span><span style="color:#ABB2BF;"> * </span><span style="color:#C678DD;">FROM</span><span style="color:#ABB2BF;"> </span><span style="color:#D19A66;">sql_store</span><span style="color:#ABB2BF;">.</span><span style="color:#D19A66;">customers</span><span style="color:#ABB2BF;"> </span><span style="color:#C678DD;">where</span><span style="color:#ABB2BF;"> last_name regexp </span><span style="color:#98C379;">&#39;field|mac&#39;</span><span style="color:#ABB2BF;">;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;">-- is null</span></span>
<span class="line"><span style="color:#C678DD;">SELECT</span><span style="color:#ABB2BF;"> * </span><span style="color:#C678DD;">FROM</span><span style="color:#ABB2BF;"> </span><span style="color:#D19A66;">sql_store</span><span style="color:#ABB2BF;">.</span><span style="color:#D19A66;">customers</span><span style="color:#ABB2BF;"> </span><span style="color:#C678DD;">where</span><span style="color:#ABB2BF;"> phone </span><span style="color:#C678DD;">is</span><span style="color:#ABB2BF;"> </span><span style="color:#C678DD;">null</span><span style="color:#ABB2BF;">;</span></span></code></pre></div><h2 id="order-by" tabindex="-1">ORDER BY <a class="header-anchor" href="#order-by" aria-label="Permalink to &quot;ORDER BY&quot;">​</a></h2><div class="language-sql"><button title="Copy Code" class="copy"></button><span class="lang">sql</span><pre class="shiki one-dark-pro"><code><span class="line"><span style="color:#C678DD;">SELECT</span><span style="color:#ABB2BF;"> * </span><span style="color:#C678DD;">FROM</span><span style="color:#ABB2BF;"> </span><span style="color:#D19A66;">sql_store</span><span style="color:#ABB2BF;">.</span><span style="color:#D19A66;">order_items</span><span style="color:#ABB2BF;"> </span><span style="color:#C678DD;">where</span><span style="color:#ABB2BF;"> order_id </span><span style="color:#56B6C2;">=</span><span style="color:#ABB2BF;"> </span><span style="color:#D19A66;">2</span><span style="color:#ABB2BF;"> </span><span style="color:#C678DD;">order by</span><span style="color:#ABB2BF;"> quantity * unit_price </span><span style="color:#C678DD;">DESC</span><span style="color:#ABB2BF;">;</span></span></code></pre></div><h2 id="limit" tabindex="-1">LIMIT <a class="header-anchor" href="#limit" aria-label="Permalink to &quot;LIMIT&quot;">​</a></h2><div class="language-sql"><button title="Copy Code" class="copy"></button><span class="lang">sql</span><pre class="shiki one-dark-pro"><code><span class="line"><span style="color:#C678DD;">SELECT</span><span style="color:#ABB2BF;"> * </span><span style="color:#C678DD;">FROM</span><span style="color:#ABB2BF;"> </span><span style="color:#D19A66;">sql_store</span><span style="color:#ABB2BF;">.</span><span style="color:#D19A66;">order_items</span><span style="color:#ABB2BF;"> </span><span style="color:#C678DD;">limit</span><span style="color:#ABB2BF;"> </span><span style="color:#D19A66;">4</span><span style="color:#ABB2BF;">;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;">-- offset 6就是偏移量，取出的是 7，8，9</span></span>
<span class="line"><span style="color:#C678DD;">SELECT</span><span style="color:#ABB2BF;"> * </span><span style="color:#C678DD;">FROM</span><span style="color:#ABB2BF;"> </span><span style="color:#D19A66;">sql_store</span><span style="color:#ABB2BF;">.</span><span style="color:#D19A66;">customers</span><span style="color:#ABB2BF;"> </span><span style="color:#C678DD;">limit</span><span style="color:#ABB2BF;"> </span><span style="color:#D19A66;">6</span><span style="color:#ABB2BF;">,</span><span style="color:#D19A66;">3</span><span style="color:#ABB2BF;">;</span></span></code></pre></div>`,10),e=[o];function t(c,B,r,y,D,i){return n(),a("div",null,e)}const C=s(p,[["render",t]]);export{F as __pageData,C as default};
